import React, { useContext, useEffect } from 'react';
import { Avatar, ListItem } from 'react-native-elements';
import { ScrollView } from 'react-native-gesture-handler';
import LoginContext from '../../../context/LoginContext';
import { SolicitacaoModel } from '../../../core/model/solicitacao.model';
import apiService from '../../../core/service/api.service';
import BaseScreen from '../../../shared/components/base/BaseScreen';

const ListarSolicitacaoScreen = () => {

    const {
        user
    } = useContext(LoginContext);

    let solicitacoes: SolicitacaoModel[] = [];

    apiService.listarSolicitacoes(user.id)
    .then((result) => {
        solicitacoes = result.data as SolicitacaoModel[];
    })
    .catch((err) => {
        console.error("ops! ocorreu um erro" + err);
    });

    return (
        <BaseScreen
            screen={
                <ScrollView>
                   {
                     solicitacoes.map((l, i) => (
                        <ListItem key={i} bottomDivider>
                            <ListItem.Title>{l.titulo}</ListItem.Title>
                        </ListItem>
                    ))
                    } 
                </ScrollView>
            }
        />
    )
}

export default ListarSolicitacaoScreen;
